    /**
     * Generate prepared last name for further processing
     *
     * @return string
     */
    public function lastName()
    {
        $name = static::firstNameMale();

        if (substr($name, -2) === 'ur') {
            $name = substr($name, 0, strlen($name) - 2);
        }

        if (substr($name, -1) !== 's') {
            $name .= 's';
        }

        return $name;
    }

